package controlstate;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum =0;
		for(int i=1;i<=1000;i++) {
			sum = sum+i;
			
		}
		System.out.println(sum);
		
		for(int i =1;i<=10;i++)
		{
			System.out.println(i);
		}
		for(int j=10;j>=1;j++)
		{
			System.out.println(j);
		}
		
	
	}

}
