package controlstate;

public class Simple_If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 103;
		
		if(num%2==0) {//true
			System.out.println("Even");
		}
		else {//false
			System.out.println("Odd");
		}
		System.out.println("The give number is : "+num);

	}

}
